              </h2>
            </div>
          </noscript>

          <Main />
          <NextScript />

          <link
            rel="stylesheet"
            href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css"
          />
        </body>
      </html>
    )
  }
}

export default MyDocument
